using System;
using System.Numerics;

namespace IntegerTypes
{
    class Program
    {
        static void Main(string[] args)
        {
            Console.WriteLine(int.MaxValue);
            // 0 - 255
            BigInteger bigNumber = BigInteger.Parse("-");

            Console.WriteLine(bigNumber + 5);
            byte studentAge = 205;
            Console.WriteLine(studentAge);

            uint studentCount = 3147000000;

            Console.WriteLine(studentCount);

            byte centuries = 20;
            ushort years = 2000;
            uint days = 730484;
            ulong hours = 17531616;
            Console.WriteLine("{0} centuries = {1} years = {2} days = {3} hours.", centuries, years, days, hours);
            //20 centuries = 2000 years = 730484 days = 17531616 hours.

        }
    }
}
